import {SafeAreaView, TouchableOpacity ,Image, Button, StyleSheet, Text, TextInput} from 'react-native';
import {createDbTable, addToDb, fetchAllData} from '../DBConnection'
import {gunsList} from '../Components/Guns'



const SkinSearch = props => {
    const [skins, setSkins] = useState('');
    const [notFinished, setNotFinished] = useState(false); // this is to ensure that the api call is finished before opening case
    const [searchSkinName, setSearchSkinName] = useState('')


    useEffect(() => {
