             let search = await fetch('https://cors-anywhere.herokuapp.com/http://csgobackpack.net/api/GetItemsList/v2/?prettyprint=true')
            .then(response => response.json())
            .then(data => data.items_list)
            let guns = keys() // all the keys for the guns
            guns.forEach(element => {
                //PhoenixGuns.push(search[element])
                console.log(search[element])
                addToDb(search[element].name, search[element].icon_url, search[element].rarity, search[element].exterior)
            });
            setNotFinished(true);
        }

        const afterFetchData = (data) => {
            console.log(data);
            setSkins(data)
            if(data === undefined || data.length == 0){
                searchGuns();
            }
        }

        createDbTable();
        fetchAllData(afterFetchData);
        
        //searchGuns()
    },[]) // only run ONCE

    const keys = () => { // this was made because importing a text file doesnt work, libraries caused many problems **EDIT guns moved to seperate file**
        let x = gunsList
            return x;
        }

    const searchHandler = () => {

    }
    return(
        <SafeAreaView> 
            <Text>{props.children}</Text>
            <TextInput 
            value = {searchSkinName}
            onChangeText = {(text) => setSearchSkinName(text)}
            placeholder = "Search Skin Name!"/>
            <Button title="Search" onPress={() => {
                    if(movieName.length > 0){
                        props.navigation.navigate('Search', {movieName: movieName});
                    }

                    else{
                        alert("Please Enter a Name! ")
                    }
                    
                }} />
        </SafeAreaView>
        
    )

}



