// }

// const fetchSpecificData = (where, successCallback) => {
//     fetchData("SELECT * from skins where name like '%?%'",[where], successCallback)
// }

const fetchAllData = (query, [], successCallback) => {
    if (Platform.OS === "web"){
        webFetchData(query, successCallback)
    }
    else{
        mobileFetchData(query, successCallback)
    }
}

const mobileFetchData = (successCallback) =>{
    db.transaction(trx => {
        trx.executeSql(
            "SELECT * from skins",[],
            (txObj, {rows: {_array}}) => successCallback(_array),
            (txObj, error) => console.log("Error", error)
        )
    })
}

const webFetchData = (successCallback) =>{
    db.transaction(trx => {
        trx.executeSql(
            "SELECT * from skins",[],
            (txObj, resultsSet) => successCallback(resultsSet.rows),
            (txObj, error) => console.log("Error", error)
        )
    })
}

export {createDbTable, addToDb, fetchAllData}
